import React from 'react';
import { format, isToday } from 'date-fns';
import { DayData, FinancialEntry } from '../types';
import './DailyView.css';

interface DailyViewProps {
  dayData: DayData;
  onAddEntry: (entry: Omit<FinancialEntry, 'id'>) => void;
  onUpdateEntry: (entry: FinancialEntry) => void;
  onDeleteEntry: (id: string) => void;
}

export const DailyView: React.FC<DailyViewProps> = ({
  dayData,
  onAddEntry,
  onUpdateEntry,
  onDeleteEntry
}) => {
  const [isAddingEntry, setIsAddingEntry] = React.useState(false);
  const [newEntry, setNewEntry] = React.useState<Omit<FinancialEntry, 'id'>>({
    date: dayData.date,
    amount: 0,
    description: '',
    type: 'expense'
  });
  const [editingEntry, setEditingEntry] = React.useState<FinancialEntry | null>(null);

  const handleAddEntry = () => {
    if (newEntry.amount <= 0 || !newEntry.description) {
      alert('Please enter a valid amount and description');
      return;
    }
    
    onAddEntry({
      ...newEntry,
      date: dayData.date
    });
    
    setIsAddingEntry(false);
    setNewEntry({
      date: dayData.date,
      amount: 0,
      description: '',
      type: 'expense'
    });
  };
  
  const handleUpdateEntry = (entry: FinancialEntry) => {
    onUpdateEntry(entry);
    setEditingEntry(null);
  };

  return (
    <div className="daily-view">
      <div className="daily-header">
        <h2>{format(dayData.date, 'EEEE, MMMM d, yyyy')}</h2>
        {isToday(dayData.date) && <span className="today-badge">Today</span>}
      </div>

      <div className="daily-summary">
        <div className="summary-card income">
          <div className="summary-label">Income</div>
          <div className="summary-value">${dayData.totalIncome.toFixed(2)}</div>
        </div>
        <div className="summary-card expense">
          <div className="summary-label">Expenses</div>
          <div className="summary-value">${dayData.totalExpenses.toFixed(2)}</div>
        </div>
        <div className="summary-card balance">
          <div className="summary-label">Daily Balance</div>
          <div className={`summary-value ${dayData.dailyBalance >= 0 ? 'positive' : 'negative'}`}>
            ${Math.abs(dayData.dailyBalance).toFixed(2)}
          </div>
        </div>
      </div>

      <div className="entries-section">
        <div className="section-header">
          <h3>Entries</h3>
          <button 
            className="add-entry-button"
            onClick={() => {
              setIsAddingEntry(true);
              setEditingEntry(null);
            }}
          >
            + Add Entry
          </button>
        </div>

        {isAddingEntry && (
          <div className="entry-form">
            <div className="form-row">
              <select
                className={`type-select ${newEntry.type}`}
                value={newEntry.type}
                onChange={(e) => setNewEntry({ ...newEntry, type: e.target.value as 'income' | 'expense' })}
              >
                <option value="income">Income</option>
                <option value="expense">Expense</option>
              </select>
              <input
                type="number"
                className="amount-input"
                placeholder="Amount"
                value={newEntry.amount || ''}
                onChange={(e) => setNewEntry({ ...newEntry, amount: parseFloat(e.target.value) || 0 })}
                min="0"
                step="0.01"
              />
            </div>
            <input
              type="text"
              className="description-input"
              placeholder="Description"
              value={newEntry.description}
              onChange={(e) => setNewEntry({ ...newEntry, description: e.target.value })}
            />
            <div className="form-actions">
              <button className="cancel-button" onClick={() => setIsAddingEntry(false)}>Cancel</button>
              <button className="add-button" onClick={handleAddEntry}>Add</button>
            </div>
          </div>
        )}

        <div className="entries-list">
          {dayData.entries.length === 0 ? (
            <div className="no-entries">No entries for this day</div>
          ) : (
            dayData.entries.map((entry) => (
              <div key={entry.id} className={`entry ${entry.type}`}>
                <div className="entry-content">
                  <div className="entry-description">{entry.description}</div>
                  <div className="entry-amount">${entry.amount.toFixed(2)}</div>
                </div>
                <div className="entry-actions">
                  {editingEntry && editingEntry.id === entry.id ? (
                    <div className="edit-entry-form">
                      <input
                        type="text"
                        value={editingEntry.description}
                        onChange={(e) => setEditingEntry({ ...editingEntry, description: e.target.value })}
                      />
                      <input
                        type="number"
                        value={editingEntry.amount}
                        onChange={(e) => setEditingEntry({ ...editingEntry, amount: parseFloat(e.target.value) || 0 })}
                        min="0"
                        step="0.01"
                      />
                      <div className="edit-actions">
                        <button onClick={() => setEditingEntry(null)}>Cancel</button>
                        <button onClick={() => handleUpdateEntry(editingEntry)}>Save</button>
                      </div>
                    </div>
                  ) : (
                    <>
                      <button
                        className="edit-button"
                        onClick={() => setEditingEntry(entry)}
                      >
                        Edit
                      </button>
                      <button
                        className="delete-button"
                        onClick={() => onDeleteEntry(entry.id)}
                      >
                        Delete
                      </button>
                    </>
                  )}
                </div>
              </div>
            ))
          )}
        </div>
      </div>
    </div>
  );
};
