import React, { useState, useEffect } from 'react';
import { Calendar } from './components/Calendar';
import { useFinancialData } from './hooks/useFinancialData';
import { FinancialEntry, CalendarViewType } from './types';
import './App.css';

const App: React.FC = () => {
  const [currentDate, setCurrentDate] = useState<Date>(new Date());
  const [view, setView] = useState<CalendarViewType>('month');
  const [error, setError] = useState<string | null>(null);
  const { monthData, yearData, addEntry, updateEntry, deleteEntry } = useFinancialData(currentDate);

  // Debug logging
  useEffect(() => {
    console.log('Month data:', monthData);
    console.log('Year data:', yearData);
  }, [monthData, yearData]);

  const handleAddEntry = (entry: Omit<FinancialEntry, 'id'>) => {
    try {
      addEntry(entry);
    } catch (err) {
      console.error('Error adding entry:', err);
      setError('Failed to add entry');
    }
  };

  const handleUpdateEntry = (entry: FinancialEntry) => {
    try {
      updateEntry(entry);
    } catch (err) {
      console.error('Error updating entry:', err);
      setError('Failed to update entry');
    }
  };

  const handleDeleteEntry = (id: string) => {
    try {
      deleteEntry(id);
    } catch (err) {
      console.error('Error deleting entry:', err);
      setError('Failed to delete entry');
    }
  };

  if (error) {
    return (
      <div className="error-container">
        <h2>Something went wrong</h2>
        <p>{error}</p>
        <button onClick={() => setError(null)}>Try Again</button>
      </div>
    );
  }

  return (
    <div className="app">
      <header className="app-header">
        <h1>Personal Budget Tracker</h1>
      </header>
      <main className="app-main">
        {monthData && yearData ? (
          <Calendar
            monthData={monthData}
            yearData={yearData}
            currentDate={currentDate}
            onDateChange={setCurrentDate}
            onAddEntry={handleAddEntry}
            onUpdateEntry={handleUpdateEntry}
            onDeleteEntry={handleDeleteEntry}
            view={view}
            onViewChange={setView}
          />
        ) : (
          <div className="loading">Loading calendar data...</div>
        )}
      </main>
      <footer className="app-footer">
        <p>Â© {new Date().getFullYear()} Personal Budget Tracker</p>
      </footer>
    </div>
  );
};

export default App;
