import React, { useState } from 'react';
import { format } from 'date-fns';
import { RecurringPayment } from '../types';
import './RecurringPayments.css';

interface RecurringPaymentsProps {
  recurringPayments: RecurringPayment[];
  onAddRecurringPayment: (payment: Omit<RecurringPayment, 'id'>) => void;
  onUpdateRecurringPayment: (payment: RecurringPayment) => void;
  onDeleteRecurringPayment: (id: string) => void;
}

export const RecurringPayments: React.FC<RecurringPaymentsProps> = ({
  recurringPayments,
  onAddRecurringPayment,
  onUpdateRecurringPayment,
  onDeleteRecurringPayment
}) => {
  const [isAddingPayment, setIsAddingPayment] = useState(false);
  const [newPayment, setNewPayment] = useState<Omit<RecurringPayment, 'id'>>({
    amount: 0,
    description: '',
    type: 'expense',
    startDate: new Date(),
    frequency: 'monthly',
    isActive: true
  });

  const handleAddPayment = () => {
    onAddRecurringPayment(newPayment);
    setIsAddingPayment(false);
    setNewPayment({
      amount: 0,
      description: '',
      type: 'expense',
      startDate: new Date(),
      frequency: 'monthly',
      isActive: true
    });
  };

  const getFrequencyLabel = (payment: RecurringPayment) => {
    switch (payment.frequency) {
      case 'daily':
        return 'Daily';
      case 'weekly':
        return `Weekly on ${format(new Date().setDate(payment.dayOfWeek || 0), 'EEEE')}`;
      case 'monthly':
        return `Monthly on day ${payment.dayOfMonth}`;
      case 'yearly':
        return 'Yearly';
      default:
        return payment.frequency;
    }
  };

  return (
    <div className="recurring-payments">
      <div className="section-header">
        <h2>Recurring Payments</h2>
        <button
          className="add-payment-button"
          onClick={() => setIsAddingPayment(true)}
        >
          + Add Recurring Payment
        </button>
      </div>

      {isAddingPayment && (
        <div className="payment-form">
          <select
            value={newPayment.type}
            onChange={(e) => setNewPayment({ ...newPayment, type: e.target.value as 'income' | 'expense' })}
          >
            <option value="income">Income</option>
            <option value="expense">Expense</option>
          </select>

          <input
            type="number"
            placeholder="Amount"
            value={newPayment.amount || ''}
            onChange={(e) => setNewPayment({ ...newPayment, amount: parseFloat(e.target.value) })}
          />

          <input
            type="text"
            placeholder="Description"
            value={newPayment.description}
            onChange={(e) => setNewPayment({ ...newPayment, description: e.target.value })}
          />

          <select
            value={newPayment.frequency}
            onChange={(e) => setNewPayment({ ...newPayment, frequency: e.target.value as 'daily' | 'weekly' | 'monthly' | 'yearly' })}
          >
            <option value="daily">Daily</option>
            <option value="weekly">Weekly</option>
            <option value="monthly">Monthly</option>
            <option value="yearly">Yearly</option>
          </select>

          {newPayment.frequency === 'weekly' && (
            <select
              value={newPayment.dayOfWeek}
              onChange={(e) => setNewPayment({ ...newPayment, dayOfWeek: parseInt(e.target.value) })}
            >
              <option value="0">Sunday</option>
              <option value="1">Monday</option>
              <option value="2">Tuesday</option>
              <option value="3">Wednesday</option>
              <option value="4">Thursday</option>
              <option value="5">Friday</option>
              <option value="6">Saturday</option>
            </select>
          )}

          {newPayment.frequency === 'monthly' && (
            <input
              type="number"
              min="1"
              max="31"
              placeholder="Day of month"
              value={newPayment.dayOfMonth || ''}
              onChange={(e) => setNewPayment({ ...newPayment, dayOfMonth: parseInt(e.target.value) })}
            />
          )}

          <input
            type="date"
            value={format(newPayment.startDate, 'yyyy-MM-dd')}
            onChange={(e) => setNewPayment({ ...newPayment, startDate: new Date(e.target.value) })}
          />

          <div className="form-actions">
            <button onClick={() => setIsAddingPayment(false)}>Cancel</button>
            <button onClick={handleAddPayment}>Add</button>
          </div>
        </div>
      )}

      <div className="payments-list">
        {recurringPayments.length === 0 ? (
          <div className="no-payments">No recurring payments</div>
        ) : (
          recurringPayments.map((payment) => (
            <div key={payment.id} className={`payment-card ${payment.type}`}>
              <div className="payment-header">
                <div className="payment-description">{payment.description}</div>
                <div className="payment-amount">${payment.amount.toFixed(2)}</div>
              </div>
              <div className="payment-details">
                <div className="payment-frequency">{getFrequencyLabel(payment)}</div>
                <div className="payment-dates">
                  Starts: {format(payment.startDate, 'MMM d, yyyy')}
                  {payment.endDate && ` - Ends: ${format(payment.endDate, 'MMM d, yyyy')}`}
                </div>
              </div>
              <div className="payment-actions">
                <button
                  className={`status-button ${payment.isActive ? 'active' : 'inactive'}`}
                  onClick={() => onUpdateRecurringPayment({ ...payment, isActive: !payment.isActive })}
                >
                  {payment.isActive ? 'Active' : 'Inactive'}
                </button>
                <button
                  className="delete-button"
                  onClick={() => onDeleteRecurringPayment(payment.id)}
                >
                  Delete
                </button>
              </div>
            </div>
          ))
        )}
      </div>
    </div>
  );
};
