import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Routes, Navigate } from 'react-router-dom';
import { Header } from './components/Header';
import { Calendar } from './components/Calendar';
import { AuthPage } from './components/auth/AuthPage';
import { useFinancialData } from './hooks/useFinancialData';
import { AuthProvider, useAuth } from './context/AuthContext';
import { FinancialEntry, CalendarViewType } from './types';
import { RecurringPayments } from './components/RecurringPayments';
import './App.css';

// Protected route component
interface ProtectedRouteProps {
  children: React.ReactNode;
}

const ProtectedRoute: React.FC<ProtectedRouteProps> = ({ children }) => {
  const { isAuthenticated, loading } = useAuth();

  if (loading) {
    return <div className="loading">Loading...</div>;
  }

  if (!isAuthenticated) {
    return <Navigate to="/auth" replace />;
  }

  return <>{children}</>;
};

// Main app component (without auth context)
const AppContent: React.FC = () => {
  const [view, setView] = useState<CalendarViewType>('month');
  const [selectedDate, setSelectedDate] = useState<Date>(new Date());
  const [showEntryForm, setShowEntryForm] = useState(false);
  const [editingEntry, setEditingEntry] = useState<FinancialEntry | null>(null);

  const {
    entries,
    addEntry,
    updateEntry,
    deleteEntry,
    recurringPayments,
    addRecurringPayment,
    updateRecurringPayment,
    deleteRecurringPayment,
    getMonthData,
    getYearData,
  } = useFinancialData();

  const handleAddEntry = (entry: Omit<FinancialEntry, 'id'>) => {
    try {
      addEntry(entry);
    } catch (err) {
      console.error('Error adding entry:', err);
      setError('Failed to add entry');
    }
  };

  const handleUpdateEntry = (entry: FinancialEntry) => {
    try {
      updateEntry(entry);
    } catch (err) {
      console.error('Error updating entry:', err);
      setError('Failed to update entry');
    }
  };

  const handleDeleteEntry = (id: string) => {
    try {
      deleteEntry(id);
    } catch (err) {
      console.error('Error deleting entry:', err);
      setError('Failed to delete entry');
    }
  };

  if (error) {
    return (
      <div className="error-container">
        <h2>Something went wrong</h2>
        <p>{error}</p>
        <button onClick={() => setError(null)}>Try Again</button>
      </div>
    );
  }

  return (
    <div className="app">
      <Header title="Personal Budget Tracker" />
      
      <main className="app-main">
        <Routes>
          <Route 
            path="/" 
            element={
              <ProtectedRoute>
                {entries && recurringPayments ? (
                  <>
                    <div className="view-controls">
                      <button onClick={() => setView('day')}>Day</button>
                      <button onClick={() => setView('week')}>Week</button>
                      <button onClick={() => setView('month')}>Month</button>
                      <button onClick={() => setView('year')}>Year</button>
                    </div>
                    <button onClick={() => setShowEntryForm(true)}>Add New Entry</button>
                  </>
                ) : (
                  <div className="loading">Loading calendar data...</div>
                )}
                <Calendar
                  view={view}
                  selectedDate={selectedDate}
                  onDateSelect={setSelectedDate}
                  entries={entries}
                  monthData={getMonthData(selectedDate)}
                  yearData={getYearData(selectedDate.getFullYear())}
                />

                {showEntryForm && (
                  <FinancialEntryForm
                    entry={editingEntry}
                    onSave={handleAddEntry}
                    onCancel={() => {
                      setShowEntryForm(false);
                      setEditingEntry(null);
                    }}
                  />
                )}

                <RecurringPayments
                  recurringPayments={recurringPayments}
                  onAddRecurringPayment={addRecurringPayment}
                  onUpdateRecurringPayment={updateRecurringPayment}
                  onDeleteRecurringPayment={deleteRecurringPayment}
                />
              </ProtectedRoute>
            } 
          />
          <Route path="/auth" element={<AuthPage />} />
          <Route path="*" element={<Navigate to="/" replace />} />
        </Routes>
      </main>
      
      <footer className="app-footer">
        <p>© {new Date().getFullYear()} Personal Budget Tracker</p>
      </footer>
    </div>
  );
};

// Root component with providers
const App: React.FC = () => {
  return (
    <Router>
      <AuthProvider>
        <AppContent />
      </AuthProvider>
    </Router>
  );
};

export default App;
